/**
 * Returns when the current number of seconds from the epoch equals the command line argument
 * given by <code>-startTime</code>. This allows multiple instances of this program, running on
 * clock synchronized nodes, to start at roughly the same time.
 *
 * @return true if the method was able to sleep for <code>-startTime</code> without
 *     interruption; false otherwise
 */
private boolean barrier() {
  long startTime = getConf().getLong("test.nnbench.starttime", 0l);
  long currentTime = System.currentTimeMillis();
  long sleepTime = startTime - currentTime;
  boolean retVal = false;

  // If the sleep time is greater than 0, then sleep and return
  if (sleepTime > 0) {
    LOG.info("Waiting in barrier for: " + sleepTime + " ms");

    try {
      Thread.sleep(sleepTime);
      retVal = true;
    } catch (Exception e) {
      retVal = false;
    }
  }

  return retVal;
}

private void doIteration(Random r, QuestionSteps qsQuestion) {
  List<String> lThings = new LinkedList<String>();

  lThings.add(qsQuestion.sID);

  try {
    // Delay
    if (r != null) Thread.sleep(r.nextInt(DELAYMAX - DELAYMIN + 1) + DELAYMIN);

    // Init question
    NameValuePairs nvp = new NameValuePairs();
    nvp.add("randomseed", "" + (qsQuestion.lRSeed + 1));
    String sSession;
    long lBefore = System.currentTimeMillis();
    if (osRemote != null) {
      om.axis.qengine.StartReturn sr =
          osRemote.start(
              qsQuestion.sID,
              qsQuestion.iMajor + "." + qsQuestion.iMinor,
              "frog",
              nvp.getNames(),
              nvp.getValues(),
              null);
      if (DEBUG) System.err.println(sr.getXHTML());
      sSession = sr.getQuestionSession();
    } else {
      om.qengine.StartReturn sr =
          osLocal.start(
              qsQuestion.sID,
              qsQuestion.iMajor + "." + qsQuestion.iMinor,
              "frog",
              nvp.getNames(),
              nvp.getValues(),
              null);
      if (DEBUG) System.err.println(sr.getXHTML());
      sSession = sr.getQuestionSession();
    }
    long lStartTime = System.currentTimeMillis() - lBefore;
    lThings.add(lStartTime + "");
    tsStart.add(lStartTime);

    // Run each sequence
    for (int iSequence = 1; ; iSequence++) {
      // Delay
      if (r != null) Thread.sleep(r.nextInt(DELAYMAX - DELAYMIN + 1) + DELAYMIN);

      // Build up params
      nvp = new NameValuePairs();
      boolean bFound = false;
      for (int iStep = 0; iStep < qsQuestion.asSteps.length; iStep++) {
        Step s = qsQuestion.asSteps[iStep];
        if (s.iSeq == iSequence) {
          nvp.add(s.sName, s.sValue);
          bFound = true;
        }
      }
      if (!bFound) break;

      // Send action
      boolean bQuestionEnd = false;
      lBefore = System.currentTimeMillis();
      if (osRemote != null) {
        om.axis.qengine.ProcessReturn pr =
            osRemote.process(sSession, nvp.getNames(), nvp.getValues());
        if (DEBUG) System.err.println(pr.getXHTML());
        bQuestionEnd = pr.isQuestionEnd();
      } else {
        om.qengine.ProcessReturn pr = osLocal.process(sSession, nvp.getNames(), nvp.getValues());
        if (DEBUG) System.err.println(pr.getXHTML());
        bQuestionEnd = pr.isQuestionEnd();
      }
      long lProcess = System.currentTimeMillis() - lBefore;
      lThings.add(lProcess + "");
      tsProcess.add(lProcess);

      if (LEAVESESSIONS) break;
      if (bQuestionEnd) break;
    }
  } catch (Throwable t) {
    t.printStackTrace();
    lThings.add("ERROR");
  }

  System.out.println(Strings.join("\t", lThings));
}

