/**
 * Monitor the job queue by executing jobs in the order that they were added to the queue and
 * waiting if there are no jobs until the next one is added.
 */
private void monitorJobQueue() {
  JobServices jobServices =
      new JobServices(ioService, analysisService, plotService, webGenomeDbService);
  while (true) {

    // Get next job from queue
    Job job = this.next();
    while (job == null) {
      try {
        Thread.sleep(JOB_EXECUTION_THREAD_SLEEP_TIME);
      } catch (InterruptedException e) {
        throw new WebGenomeSystemException(
            "Error putting job execution " + "thread to sleep", e);
      }
      job = this.next();
    }

    // Set start time and persist
    try {
      LOGGER.info("Starting job with id '" + job.getId() + "'");
      job.setStartDate(new Date());
      webGenomeDbService.saveOrUpdateJob(job);
    } catch (Exception e) {
      LOGGER.error("Unable to save job state", e);
    }

    // Execute job
    try {
      job.execute(jobServices);
      LOGGER.info("Job '" + job.getId() + "' successfully completed");
    } catch (Exception e) {
      LOGGER.warn("Job '" + job.getId() + "' failed");
      LOGGER.warn(e);
      Throwable throwable = e;
      while (throwable != null) {
        e.printStackTrace(System.err);
        throwable = throwable.getCause();
      }
      String exceptionMsg = e.getMessage();
      String msg = Job.JOB_EXECUTION_FAILURE_MESSAGE;
      if (exceptionMsg != null && exceptionMsg.length() > 0) {
        msg += ": " + exceptionMsg;
      }
      job.setTerminationMessage(msg);
    }

    // Set end time and persist
    try {
      job.setEndDate(new Date());
      webGenomeDbService.saveOrUpdateJob(job);
    } catch (Exception e) {
      LOGGER.error("Unable to save job state", e);
    }
  }
}

