/**
 * Wait for the specified number of milliseconds. Delays the current thread for the specified
 * period of time. Can not be interrupted (but it does preserve the interrupted state).
 *
 * @param period time to wait in ms
 */
public static void msDelay(long period) {
  if (period <= 0) return;
  long end = System.currentTimeMillis() + period;
  boolean interrupted = false;
  do {
    try {
      Thread.sleep(period);
    } catch (InterruptedException ie) {
      interrupted = true;
    }
    period = end - System.currentTimeMillis();
  } while (period > 0);
  if (interrupted) Thread.currentThread().interrupt();
}

/** Run the event checking thread. */
public void run() {
  // since we might be running while the component manager is still being created and populated,
  // such as at server startup, wait here for a
  // complete component manager
  ComponentManager.waitTillConfigured();

  // find the latest event in the db
  initLastEvent();

  // loop till told to stop
  while ((!m_threadStop) && (!Thread.currentThread().isInterrupted())) {
    final String serverInstance = serverConfigurationService().getServerIdInstance();
    final String serverId = serverConfigurationService().getServerId();

    try {
      // write any batched events
      Collection myEvents = new Vector();
      if (m_batchWrite) {
        synchronized (m_eventQueue) {
          if (m_eventQueue.size() > 0) {
            myEvents.addAll(m_eventQueue);
            m_eventQueue.clear();
          }
        }

        if (myEvents.size() > 0) {
          if (M_log.isDebugEnabled())
            M_log.debug("writing " + myEvents.size() + " batched events");
          writeBatchEvents(myEvents);
        }
      }

      if (M_log.isDebugEnabled()) M_log.debug("checking for events > " + m_lastEventSeq);
      // check the db for new events
      // Note: the events may not all have sessions, so to get them we need an outer join.
      // TODO: switch to a "view" read once that's established, for now, a join -ggolden
      String statement = clusterEventTrackingServiceSql.getEventSql();

      // we might want a left join, which would get us records from non-sessions, which the above
      // mysql code does NOT give -ggolden
      // select e.EVENT_ID,e.EVENT_DATE,e.EVENT,e.REF,e.SESSION_ID,e.EVENT_CODE,s.SESSION_SERVER
      // from SAKAI_EVENT e
      // left join SAKAI_SESSION s on (e.SESSION_ID = s.SESSION_ID)
      // where EVENT_ID > 0

      // send in the last seq number parameter
      Object[] fields = new Object[1];
      fields[0] = Long.valueOf(m_lastEventSeq);

      List events =
          sqlService()
              .dbRead(
                  statement,
                  fields,
                  new SqlReader() {
                    public Object readSqlResultRecord(ResultSet result) {
                      try {
                        // read the Event
                        long id = result.getLong(1);
                        Time date =
                            timeService()
                                .newTime(result.getTimestamp(2, sqlService().getCal()).getTime());
                        String function = result.getString(3);
                        String ref = result.getString(4);
                        String session = result.getString(5);
                        String code = result.getString(6);
                        String context = result.getString(7);
                        String eventSessionServerId = result.getString(8);

                        // for each one (really, for the last one), update the last event seen seq
                        // number
                        if (id > m_lastEventSeq) {
                          m_lastEventSeq = id;
                        }

                        boolean nonSessionEvent = session.startsWith("~");
                        String userId = null;
                        boolean skipIt = false;

                        if (nonSessionEvent) {
                          String[] parts = StringUtil.split(session, "~");
                          userId = parts[2];

                          // we skip this event if it came from our server
                          skipIt = serverId.equals(parts[1]);
                        }

                        // for session events, if the event is from this server instance,
                        // we have already processed it and can skip it here.
                        else {
                          skipIt = serverInstance.equals(eventSessionServerId);
                        }

                        if (skipIt) {
                          return null;
                        }

                        // Note: events from outside the server don't need notification info,
                        // since notification is processed only on internal
                        // events -ggolden
                        BaseEvent event =
                            new BaseEvent(
                                id,
                                function,
                                ref,
                                context,
                                "m".equals(code),
                                NotificationService.NOTI_NONE);
                        if (nonSessionEvent) {
                          event.setUserId(userId);
                        } else {
                          event.setSessionId(session);
                        }

                        return event;
                      } catch (SQLException ignore) {
                        return null;
                      }
                    }
                  });

      // for each new event found, notify observers
      for (int i = 0; i < events.size(); i++) {
        Event event = (Event) events.get(i);
        notifyObservers(event, false);
      }
    } catch (Exception e) {
      M_log.warn("run: will continue: ", e);
    }

    // take a small nap
    try {
      Thread.sleep(m_period);
    } catch (Exception ignore) {
    }
  }
}

/**
 * This method makes a best effort to fetch a piece of instance metadata.
 *
 * @param key the name of the metadata to fetch
 * @return value of the metadata item
 */
public static String getInstanceUserdata() throws IOException {
  int retries = 0;
  while (true) {
    try {
      URL url = new URL("http://169.254.169.254/latest/user-data/");
      InputStreamReader rdr = new InputStreamReader(url.openStream());
      StringWriter wtr = new StringWriter();
      char[] buf = new char[1024];
      int bytes;
      while ((bytes = rdr.read(buf)) > -1) {
        if (bytes > 0) {
          wtr.write(buf, 0, bytes);
        }
      }
      rdr.close();
      return wtr.toString();
    } catch (IOException ex) {
      if (retries == 5) {
        logger.debug("Problem getting user data, retries exhausted...");
        return null;
      } else {
        logger.debug("Problem getting user data, retrying...");
        try {
          Thread.sleep((int) Math.pow(2.0, retries) * 1000);
        } catch (InterruptedException e) {
        }
        retries++;
      }
    }
  }
}

public static boolean getCurrentPosition(String filePath) {
  Log.v(TAG, "GetCurrentPosition - " + filePath);
  int currentPosition = 0;
  long t1 = 0;
  long t2 = 0;
  MediaPlayer mp = new MediaPlayer();
  try {
    mp.setDataSource(filePath);
    Log.v(TAG, "start playback");
    mp.prepare();
    mp.start();
    t1 = SystemClock.uptimeMillis();
    Thread.sleep(10000);
    mp.pause();
    Thread.sleep(MediaNames.PAUSE_WAIT_TIME);
    t2 = SystemClock.uptimeMillis();
  } catch (Exception e) {
    Log.v(TAG, e.toString());
  }
  currentPosition = mp.getCurrentPosition();
  mp.stop();
  mp.release();
  Log.v(TAG, "mp currentPositon = " + currentPosition + " play duration = " + (t2 - t1));
  // The currentposition should be within 10% of the sleep time
  // For the very short mp3, it should return the length instead of 10 seconds
  if (filePath.equals(MediaNames.SHORTMP3)) {
    if (currentPosition < 1000) return true;
  }
  if ((currentPosition < ((t2 - t1) * 1.2)) && (currentPosition > 0)) return true;
  else return false;
}

