/** Runs this thread. The thread keeps running until the expiry strategy is stopped. */
public void run() {

  Log.log_1402(_instanceNum);

  long now = System.currentTimeMillis();
  long next = now + _precision;

  while (!_stop) {
    boolean interrupted;
    long sleep = next - now;
    if (sleep > 0) {
      Log.log_1404(_instanceNum, sleep);
      try {
        Thread.sleep(sleep);
        interrupted = false;

        // Sleep was interrupted
      } catch (InterruptedException exception) {
        interrupted = true;
      }

      // Determine how much time we spent since we started sleeping
      long after = System.currentTimeMillis();
      long slept = after - now;
      now = after;

      // Perform logging
      if (interrupted) {
        Log.log_1405(_instanceNum, slept);
      } else {
        Log.log_1406(_instanceNum, slept);
      }
    }

    // If we should stop, then exit the loop
    if (_stop) {
      break;
    }

    while (next <= now) {
      Log.log_1407(_instanceNum);
      doTick();
      now = System.currentTimeMillis();
      next += _precision;
    }
  }

  Log.log_1403(_instanceNum);
}

public void run() {

  while (true) {

    for (Enumeration e = queue.keys(); e.hasMoreElements(); ) {
      try {

        String key = (String) e.nextElement();
        Watcher ebw = (Watcher) queue.get(key);
        String tl = ebw.getTime();

        if (tl.length() > 0) {
          tl += " left";
        }

        // Go ahead and remove stuff first.
        if (tl.startsWith("Auction has ended")) {
          setStatus("Removing " + key);
          queue.remove(key);
          prop.remove("item." + key);
        } else {
          setStatus("Checking on " + key + " " + tl);

          if (ebw.hasChanged()) {
            setStatus(key + " has changed.");

            // Figure out if we need a Was line
            String was = "";
            was = ebw.oldStatus();
            if (was.length() > 0) {
              was = "\n\nWas:\n" + was + "\n";
            } else {
              // If we didn't have a was, we didn't have
              // a description for this item in the
              // properties file (maybe)
              prop.put("item." + key, ebw.describe());
            }

            String msg = "Item " + key + " has changed:\n" + ebw.currentStatus() + was + tl;
            popUp(msg);
          } // Item info has changed
        } // Auction for item has not ended

      } catch (Exception cept) {
        System.err.println("Error checking stuff: " + cept);
        cept.printStackTrace();
      }
    } // Looking through all of the queued items

    // Wait
    try {
      Thread.sleep(300 * 1000);
    } catch (Exception e) {
      // Don't care
      System.err.println("Error! " + e);
    } // sleep (and exception)
  } // Infinite Loop(tm)
} // Thread run

