      Date now = new Date(System.currentTimeMillis());
      System.out.println("UktParserThread " + dateFormat.format(now));
    }
  } catch (InterruptedException e) {
    e.printStackTrace();
  }
}

/** Tests mod & access time in DFS. */
public void testTimes() throws IOException {
  Configuration conf = new Configuration();
  final int MAX_IDLE_TIME = 2000; // 2s
  conf.setInt("ipc.client.connection.maxidletime", MAX_IDLE_TIME);
  conf.setInt("heartbeat.recheck.interval", 1000);
  conf.setInt("dfs.heartbeat.interval", 1);

  MiniDFSCluster cluster = new MiniDFSCluster(conf, numDatanodes, true, null);
  cluster.waitActive();
  final int nnport = cluster.getNameNodePort();
  InetSocketAddress addr = new InetSocketAddress("localhost", cluster.getNameNodePort());
  DFSClient client = new DFSClient(addr, conf);
  DatanodeInfo[] info = client.datanodeReport(DatanodeReportType.LIVE);
  assertEquals("Number of Datanodes ", numDatanodes, info.length);
  FileSystem fileSys = cluster.getFileSystem();
  int replicas = 1;
  assertTrue(fileSys instanceof DistributedFileSystem);

  try {
    //
    // create file and record atime/mtime
    //
    System.out.println("Creating testdir1 and testdir1/test1.dat.");
    Path dir1 = new Path("testdir1");
    Path file1 = new Path(dir1, "test1.dat");
    FSDataOutputStream stm = writeFile(fileSys, file1, replicas);
    FileStatus stat = fileSys.getFileStatus(file1);
    long atimeBeforeClose = stat.getAccessTime();
    String adate = dateForm.format(new Date(atimeBeforeClose));
    System.out.println(
        "atime on " + file1 + " before close is " + adate + " (" + atimeBeforeClose + ")");
    assertTrue(atimeBeforeClose != 0);
    stm.close();

    stat = fileSys.getFileStatus(file1);
    long atime1 = stat.getAccessTime();
    long mtime1 = stat.getModificationTime();
    adate = dateForm.format(new Date(atime1));
    String mdate = dateForm.format(new Date(mtime1));
    System.out.println("atime on " + file1 + " is " + adate + " (" + atime1 + ")");
    System.out.println("mtime on " + file1 + " is " + mdate + " (" + mtime1 + ")");
    assertTrue(atime1 != 0);

    //
    // record dir times
    //
    stat = fileSys.getFileStatus(dir1);
    long mdir1 = stat.getAccessTime();
    assertTrue(mdir1 == 0);

    // set the access time to be one day in the past
    long atime2 = atime1 - (24L * 3600L * 1000L);
    fileSys.setTimes(file1, -1, atime2);

    // check new access time on file
    stat = fileSys.getFileStatus(file1);
    long atime3 = stat.getAccessTime();
    String adate3 = dateForm.format(new Date(atime3));
    System.out.println("new atime on " + file1 + " is " + adate3 + " (" + atime3 + ")");
    assertTrue(atime2 == atime3);
    assertTrue(mtime1 == stat.getModificationTime());

    // set the modification time to be 1 hour in the past
    long mtime2 = mtime1 - (3600L * 1000L);
    fileSys.setTimes(file1, mtime2, -1);

    // check new modification time on file
    stat = fileSys.getFileStatus(file1);
    long mtime3 = stat.getModificationTime();
    String mdate3 = dateForm.format(new Date(mtime3));
    System.out.println("new mtime on " + file1 + " is " + mdate3 + " (" + mtime3 + ")");
    assertTrue(atime2 == stat.getAccessTime());
    assertTrue(mtime2 == mtime3);

    // shutdown cluster and restart
    cluster.shutdown();
    try {
      Thread.sleep(2 * MAX_IDLE_TIME);
    } catch (InterruptedException e) {
    }
    cluster = new MiniDFSCluster(nnport, conf, 1, false, true, null, null, null);
    cluster.waitActive();
    fileSys = cluster.getFileSystem();

    // verify that access times and modification times persist after a
    // cluster restart.
    System.out.println("Verifying times after cluster restart");
    stat = fileSys.getFileStatus(file1);
    assertTrue(atime2 == stat.getAccessTime());
    assertTrue(mtime3 == stat.getModificationTime());

    cleanupFile(fileSys, file1);
    cleanupFile(fileSys, dir1);
  } catch (IOException e) {
    info = client.datanodeReport(DatanodeReportType.ALL);
    printDatanodeReport(info);
    throw e;
  } finally {
    fileSys.close();
    cluster.shutdown();
  }
}

@Test
public void testSubmitEcho() throws Exception {
  File outFile = createNetworkTempFile("JnaSessionIntegrationTest-", ".out");
  Session session = factory.getSession();
  session.init(null);
  try {
    JobTemplate template = session.createJobTemplate();
    template.setRemoteCommand("sh");
    template.setOutputPath(":" + outFile.getAbsolutePath());
    template.setJoinFiles(true);
    template.setArgs(Arrays.asList("-c", "echo \"Hello world.\""));

    String jobId = session.runJob(template);
    System.out.println(String.format("Job id %s", jobId));
    session.deleteJobTemplate(template);

    System.out.println("Waiting for job to run: " + jobId);
    int remotePs = Session.QUEUED_ACTIVE;

    List<Integer> runningStatuses = Arrays.asList(Session.QUEUED_ACTIVE, Session.RUNNING);

    while (runningStatuses.contains(remotePs)) {
      Thread.sleep(30 * 1000L);
      remotePs = session.getJobProgramStatus(jobId);
    }

    Assert.assertEquals(remotePs, Session.DONE, "Job status is not DONE.");

    JobInfo jobInfo = session.wait(jobId, Session.TIMEOUT_NO_WAIT);

    Assert.assertTrue(jobInfo.hasExited(), String.format("Job did not exit cleanly: %s", jobId));
    Assert.assertEquals(
        jobInfo.getExitStatus(), 0, String.format("Exit status for jobId %s is non-zero", jobId));
    if (jobInfo.hasSignaled())
      Assert.fail(
          String.format(
              "JobId %s exited with signal %s and core dump flag %s",
              jobId, jobInfo.getTerminatingSignal(), jobInfo.hasCoreDump()));
    Assert.assertFalse(jobInfo.wasAborted(), String.format("Job was aborted: %s", jobId));
  } finally {
    session.exit();
  }

  Assert.assertTrue(
      FileUtils.waitFor(outFile, 120), "File not found: " + outFile.getAbsolutePath());
  System.out.println("--- output ---");
  System.out.println(FileUtils.readFileToString(outFile));
  System.out.println("--- output ---");
  Assert.assertTrue(outFile.delete(), "Unable to delete " + outFile.getAbsolutePath());
  System.out.println("Validating that we reached the end of the test without exit.");
}

