/** Run the maintenance thread. Every m_checkEvery seconds, check for expired sessions. */
public void run() {
  // since we might be running while the component manager is still being created and populated,
  // such as at server
  // startup, wait here for a complete component manager
  ComponentManager.waitTillConfigured();

  while (!m_maintenanceCheckerStop) {
    try {
      for (Map.Entry<String, MutableLong> entry : expirationTimeSuggestionMap.entrySet()) {
        if (entry.getValue().longValue() < System.currentTimeMillis()) {
          MySession s = (MySession) m_sessions.get(entry.getKey());
          if (M_log.isDebugEnabled()) M_log.debug("checking session " + s.getId());
          if (s.isInactive()) {
            if (M_log.isDebugEnabled()) M_log.debug("invalidating session " + s.getId());
            synchronized (s) {
              s.invalidate();
            }
          }
        }
      }
    } catch (Exception e) {
      M_log.warn("run(): exception: " + e);
    }

    // cycle every REFRESH seconds
    if (!m_maintenanceCheckerStop) {
      try {
        Thread.sleep(m_checkEvery * 1000L);
      } catch (Exception ignore) {
      }
    }
  }
}

/**
 * Read (potentially tailing) an input stream
 *
 * @param is InputStream to read from
 * @param buf Buffer to read into
 * @param off Offset in buffer to read into
 * @param len Number of bytes to read
 * @param tp policy to use if we hit EOF
 * @return number of bytes read
 */
private int tailRead(InputStream is, byte[] buf, int off, int len, tailPolicy tp)
    throws TTransportException {
  int orig_len = len;
  try {
    int retries = 0;
    while (len > 0) {
      int cnt = is.read(buf, off, len);
      if (cnt > 0) {
        off += cnt;
        len -= cnt;
        retries = 0;
        cs.skip(cnt); // remember that we read so many bytes
      } else if (cnt == -1) {
        // EOF
        retries++;

        if ((tp.retries_ != -1) && tp.retries_ < retries) return (orig_len - len);

        if (tp.timeout_ > 0) {
          try {
            Thread.sleep(tp.timeout_);
          } catch (InterruptedException e) {
          }
        }
      } else {
        // either non-zero or -1 is what the contract says!
        throw new TTransportException("Unexpected return from InputStream.read = " + cnt);
      }
    }
  } catch (IOException iox) {
    throw new TTransportException(iox.getMessage(), iox);
  }

  return (orig_len - len);
}

