private int runAndWatch(String remoteHost, String remoteCommand) {
  int exitStatus = -1;
  try {
    Process proc = new ProcessBuilder(CMD_SSH, remoteHost, remoteCommand).start();
    watchProcStream(proc.getInputStream(), System.out);
    watchProcStream(proc.getErrorStream(), System.err);
    exitStatus = proc.waitFor();
  } catch (InterruptedException intExc) {
    LOG.warn("got thread interrupt error", intExc);
  } catch (IOException ioExc) {
    LOG.warn("got i/o error", ioExc);
  }
  return exitStatus;
}

public void start() {
  ProcessBuilder builder = new ProcessBuilder(_args);
  try {
    assert !_inherit || (_out == null);
    _process = builder.start();
    if (_inherit) inheritIO(_process, null);
    if (_out != null) persistIO(_process, _out, _err);
  } catch (IOException e) {
    throw Log.errRTExcept(e);
  }
}

@Override
public Annotation annotate(File file, String revision) throws IOException {
  SAXParserFactory factory = SAXParserFactory.newInstance();
  SAXParser saxParser = null;
  try {
    saxParser = factory.newSAXParser();
  } catch (Exception ex) {
    IOException err = new IOException("Failed to create SAX parser", ex);
    throw err;
  }

  ArrayList<String> argv = new ArrayList<String>();
  ensureCommand(CMD_PROPERTY_KEY, CMD_FALLBACK);
  argv.add(cmd);
  argv.add("annotate");
  argv.add("--trust-server-cert");
  argv.add("--non-interactive");
  argv.add("--xml");
  if (revision != null) {
    argv.add("-r");
    argv.add(revision);
  }
  argv.add(escapeFileName(file.getName()));
  ProcessBuilder pb = new ProcessBuilder(argv);
  pb.directory(file.getParentFile());
  Process process = null;
  BufferedInputStream in = null;
  Annotation ret = null;
  try {
    process = pb.start();
    in = new BufferedInputStream(process.getInputStream());

    AnnotateHandler handler = new AnnotateHandler(file.getName());
    try {
      saxParser.parse(in, handler);
      ret = handler.annotation;
    } catch (Exception e) {
      OpenGrokLogger.getLogger()
          .log(Level.SEVERE, "An error occurred while parsing the xml output", e);
    }
  } finally {
    IOUtils.close(in);
    if (process != null) {
      try {
        process.exitValue();
      } catch (IllegalThreadStateException e) {
        // the process is still running??? just kill it..
        process.destroy();
      }
    }
  }
  return ret;
}

private String run(List<String> cmd) {
  String ret = "";
  try {
    ProcessBuilder b = new ProcessBuilder(cmd);
    Process p = b.start();
    BufferedReader reader = new BufferedReader(new InputStreamReader(p.getInputStream()));
    String s = reader.readLine();
    while (s != null) {
      ret = ret.concat(s + "\n");
      s = reader.readLine();
    }
    p.waitFor();
  } catch (IOException e) {
    e.printStackTrace();
  } catch (InterruptedException e) {
    e.printStackTrace();
  }
  return ret;
}

public int execute(CommandOutputListener commandOutputListener)
    throws InterruptedException, IOException {
  ProcessBuilder builder = new ProcessBuilder(this.args);
  Process process = builder.start();

  Thread stdoutWatcher =
      new ProcessOutputWatcher(
          new BufferedReader(new InputStreamReader(process.getInputStream())),
          commandOutputListener);
  Thread stderrWatcher =
      new ProcessOutputWatcher(
          new BufferedReader(new InputStreamReader(process.getErrorStream())),
          commandOutputListener);

  stdoutWatcher.start();
  stderrWatcher.start();

  process.waitFor();

  return process.exitValue();
}

