/**
 * Run a given command in a subprocess, including threads to copy its stdout and stderr to our
 * stdout and stderr.
 *
 * @param command the command and its arguments
 * @param env the environment to run the process in
 * @return a handle on the process
 * @throws IOException
 */
static Process runClient(List<String> command, Map<String, String> env) throws IOException {
  ProcessBuilder builder = new ProcessBuilder(command);
  if (env != null) {
    builder.environment().putAll(env);
  }
  Process result = builder.start();
  return result;
}

/**
 * Runs given command with arguments and returns the output as a String
 *
 * @param cmd command to run, this will usually be the script that is ran
 * @param argment the first argument, this will be the main qstat query
 * @param userString the user name pattern to filter the users by in the query
 * @return
 */
public static String runScriptAndGetOutput(String cmd, String argment, String userString) {
  String result = "";

  ProcessBuilder processBuilder;
  if (userString != null && !userString.equals("")) {
    processBuilder = new ProcessBuilder(cmd, argment, userString);
  } else {
    processBuilder = new ProcessBuilder(cmd, argment);
  }

  processBuilder.redirectErrorStream(true);
  try {
    process = processBuilder.start();
  } catch (IOException ioe) {
    System.out.println(ioe);
    process = null;
  }

  if (process != null) {
    BufferedReader brOut = new BufferedReader(new InputStreamReader(process.getInputStream()));
    String line = null;
    try {
      while ((line = brOut.readLine()) != null) {
        result += "\n" + line;
      }
      brOut.close();
    } catch (IOException ioe) {
      System.out.println(ioe);
    }
  }

  int startOfXmlIndex = result.indexOf("<?xml version='1.0'?");
  if (startOfXmlIndex == -1) {
    System.out.println("Can not find XML header.");
  } else {
    result = result.substring(startOfXmlIndex);
  }

  return result;
}

