public boolean run(AbstractUIProcessHandler handler, VariableSubstitutor vs) {
  this.handler = handler;

  List<String> params = new ArrayList<String>(this.arguments.size() + 1);

  params.add(vs.substitute(this.filename, "plain"));

  for (String argument : this.arguments) {
    params.add(vs.substitute(argument, "plain"));
  }

  ProcessBuilder pb = new ProcessBuilder(params);
  Map<String, String> environment = pb.environment();
  for (String envvar : envvariables) {
    String ev = vs.substitute(envvar, "plain");
    int i = ev.indexOf("=");
    if (i > 0) {
      environment.put(ev.substring(0, i), ev.substring(i + 1));
    }
  }

  try {

    Process p = pb.start();

    OutputMonitor stdoutMon = new OutputMonitor(this.handler, p.getInputStream(), false);
    OutputMonitor stderrMon = new OutputMonitor(this.handler, p.getErrorStream(), true);
    Thread stdoutThread = new Thread(stdoutMon);
    Thread stderrThread = new Thread(stderrMon);
    stdoutThread.setDaemon(true);
    stderrThread.setDaemon(true);
    stdoutThread.start();
    stderrThread.start();

    try {
      int exitStatus = p.waitFor();

      stopMonitor(stdoutMon, stdoutThread);
      stopMonitor(stderrMon, stderrThread);

      if (exitStatus != 0) {
        if (this.handler.askQuestion(
                "Process execution failed",
                "Continue anyway?",
                AbstractUIHandler.CHOICES_YES_NO,
                AbstractUIHandler.ANSWER_YES)
            == AbstractUIHandler.ANSWER_NO) {
          return false;
        }
      }
    } catch (InterruptedException ie) {
      p.destroy();
      this.handler.emitError("process interrupted", ie.toString());
      return false;
    }
  } catch (IOException ioe) {
    this.handler.emitError("I/O error", ioe.toString());
    return false;
  }

  return true;
}

private void setStartScriptPermissions(File binDirectory) {
  if (SystemUtil.isWindowsSystem()) {
    // do we have filepermissions on windows
    return;
  }
  ProcessBuilder processBuilder =
      new ProcessBuilder(
          "chmod", "755", "catalina.sh", "setclasspath.sh", "startup.sh", "shutdown.sh");
  processBuilder.directory(binDirectory);
  processBuilder.redirectErrorStream(true);
  int exitValue = -1;
  try {
    Process p = processBuilder.start();
    exitValue = p.waitFor();
  } catch (InterruptedException e) {
    log.error(e.getMessage(), e);
    throw new TomcatSetupException(e.getMessage(), e);
  } catch (IOException e) {
    log.error(e.getMessage(), e);
    throw new TomcatSetupException(e.getMessage(), e);
  }
  log.debug("SetStartScriptPermission return value " + exitValue);
}

public void runCmdLine(@NonNull String[] command, @Nullable Map<String, String> envVariableMap)
    throws IOException, InterruptedException, LoggedErrorException {
  printCommand(command);

  // launch the command line process
  ProcessBuilder processBuilder = new ProcessBuilder(command);
  if (envVariableMap != null) {
    Map<String, String> env = processBuilder.environment();
    for (Map.Entry<String, String> entry : envVariableMap.entrySet()) {
      env.put(entry.getKey(), entry.getValue());
    }
  }

  Process process = processBuilder.start();

  // get the output and return code from the process
  OutputGrabber grabber = new OutputGrabber();

  int returnCode =
      GrabProcessOutput.grabProcessOutput(
          process,
          GrabProcessOutput.Wait
              .WAIT_FOR_READERS, // we really want to make sure we get all the output!
          grabber);

  if (returnCode != 0) {
    throw new LoggedErrorException(returnCode, grabber.mErrors, Joiner.on(' ').join(command));
  }
}

public void executeTagger(String ontologyPath) throws ComplexMappingException {
  taggerLoaded = true;
  List<String> resultingTerm = new ArrayList<String>();
  resultingTerm.add("java");
  resultingTerm.add("-jar");
  resultingTerm.add("-Xms1500m");
  resultingTerm.add("-Xmx1500m");
  resultingTerm.add("Tagger.jar");
  resultingTerm.add("\"" + ontologyPath + "\"");
  File directory = new File("tagger/Tagger.jar");
  ProcessBuilder p = new ProcessBuilder(resultingTerm);
  p.redirectErrorStream(true);
  p.directory(new File(directory.getParent()));
  try {
    final Process x = p.start();

    // scan whatever the matcher outputs on the command line
    // if any error occurs, this can be discovered by the output
    ExecutorService executorService = Executors.newSingleThreadExecutor();
    executorService.execute(
        new Runnable() {
          public void run() {
            Scanner scanner = new Scanner(x.getInputStream());
            while (scanner.hasNextLine()) {
              System.out.println(scanner.nextLine());
            }
            scanner.close();
          }
        });

    // process has been terminated
    x.waitFor();
    executorService.shutdown();
  } catch (Exception e) {
    throw new ComplexMappingException(
        ExceptionType.TAGGER_EXCEPTION, "Cannot create semantic relation file.", e);
  }
}

/**
