private static MatchResult matchSystemFile(
    final String pSystemFile, final String pPattern, final int pHorizon)
    throws SystemUtilsException {
  InputStream in = null;
  try {
    final Process process =
        new ProcessBuilder(new String[] {"/system/bin/cat", pSystemFile}).start();

    in = process.getInputStream();
    final Scanner scanner = new Scanner(in);

    final boolean matchFound = scanner.findWithinHorizon(pPattern, pHorizon) != null;
    if (matchFound) {
      return scanner.match();
    } else {
      throw new SystemUtilsException();
    }
  } catch (final IOException e) {
    throw new SystemUtilsException(e);
  } finally {
    StreamUtils.close(in);
  }
}

