private void preprocess() throws ParserException {
  typedefs = new LinkedList<String>();
  Process p;
  String line;

  // this is necessary
  // the environment is modified only AFTER the command is
  // executed!
  String command =
      Stanse.getInstance().getRootDirectory()
          + File.separator
          + "bin"
          + File.separator
          + "stpreproc";
  String jobEntry = "{" + args.get(0) + "}";
  for (int i = 1; i < args.size(); ++i) {
    jobEntry += ",{";
    jobEntry += args.get(i);
    jobEntry += "}";
  }
  ProcessBuilder builder = new ProcessBuilder("perl", command, jobEntry);
  Map<String, String> env = builder.environment();
  if (env.containsKey("Path")) {
    env.put("PATH", env.get("Path"));
    env.remove("Path");
  }
  env.put(
      "PATH",
      env.get("PATH")
          + File.pathSeparator
          + Stanse.getInstance().getRootDirectory()
          + File.separator
          + "bin");
  try {
    p = builder.start();

    BufferedReader reader = new BufferedReader(new InputStreamReader(p.getInputStream()));
    while ((line = reader.readLine()) != null) typedefs.add(line);
    reader.close();
  } catch (IOException e) {
    throw new ParserException("preprocessor: " + e.getLocalizedMessage(), e);
  }

  try {
    int retval = p.waitFor();
    if (retval != 0) {
      ClassLogger.error(this, "preprocessor failed. stderr follows:");
      try {
        StreamAlgo.copy(p.getErrorStream(), System.err);
      } catch (IOException e) {
        ClassLogger.error(this, "Failed to dump stderr");
      }
      ClassLogger.error(this, "========== stderr end");
      throw new ParserException("preprocessor failed: " + Integer.toString(retval));
    }
  } catch (InterruptedException e) {
    throw new ParserException("preprocessor", e);
  }
  p.destroy();
}

