  try {
    do {
      if (!line.equals("")) {
        line.trim();

        synchronized (this) {
          System.out.println(line);
        }
      }
    } while ((line = reader.readLine()) != null);
  } catch (Exception e) {
    if (MPJRun.DEBUG && MPJRun.logger.isDebugEnabled()) {
      MPJRun.logger.debug("outputHandler =>" + e.getMessage());
    }
    e.printStackTrace();
  }
}

/**
 * Get the thread dump of a given Java processs.
 *
 * @param pid PID of the Java process
 * @return Its thread dumps
 * @throws IOException If the thread dump cannot be fetched
 */
public final String getThreadDump(int pid) throws IOException {
  ProcessBuilder pb = new ProcessBuilder(getJstack().getAbsolutePath(), Integer.toString(pid));
  pb.redirectErrorStream(true);
  Process p = pb.start();

  Reader r = new InputStreamReader(p.getInputStream());
  BufferedReader br = new BufferedReader(r);

  StringBuilder sb = new StringBuilder();
  for (String line = br.readLine(); line != null; line = br.readLine()) {
    sb.append(line);
  }

  return sb.toString();
}

