/**
 * Kick off the worker thread to monitor differences in the backend file
 *
 * <p>First check if there are even differences in the timestamps
 */
public void run() {
  boolean keepRunning = true;
  synchronized (runningLock) {
    isRunning = true;
  }

  File repoDir = new File(filename);
  Log.fine(Log.FAC_SYNC, "repoDir: " + repoDir);
  File repoFile = new File(filename + "/repoFile1");

  String commandCreateDiff =
      "ccnnamelist repoFile1 > names ; sort names > newnames ; rm names ; :>> oldnames ; diff newnames oldnames > diffNames ; mv newnames oldnames";
  String commandCreateDiffFinal;

  long lastReadTime = -1;
  long repoFileTime;
  long lastModified = -1;

  FileLock fileLock = null;

  while (keepRunning) {

    try {
      fileChannel = new RandomAccessFile(filename + "/sync.lock", "rw").getChannel();
      fileLock = fileChannel.lock();
    } catch (FileNotFoundException e1) {
      Log.severe("CCNR_DIRECTORY setting = {0}: file not found. Exiting.", filename);
      System.exit(1);
    } catch (IOException e) {
      try {
        fileLock.release();
      } catch (IOException e1) {
        // already in an error state...
        Log.warning("Error releasing lock for Sync API file processing: {0}", e1.getMessage());
      }
      Log.severe(
          "Exception when trying to acquire lock to process new names for Sync API: {0}. Exiting.",
          e.getMessage());
      System.exit(1);
    }

    repoFileTime = repoFile.lastModified();

    // System.out.println("on a new loop! repoFileTime = "+repoFileTime+" lastModified =
    // "+lastModified+" lastReadTime = "+lastReadTime);

    // is the diff file new to me?
    for (String diffFiles : repoDir.list()) {
      if (diffFiles.startsWith("diffNames.")) {
        // this is a diff names to process
        Log.fine(Log.FAC_SYNC, "have a diffFile: " + diffFiles);
        if (lastModified < Long.parseLong(diffFiles.substring(10)) || lastModified == -1) {
          // we need to read in this file
          Log.fine(Log.FAC_SYNC, "this is a new diffFile! reading it in!");
          lastModified = Long.parseLong(diffFiles.substring(10));

          // read in file here
          try {

            Log.fine(Log.FAC_SYNC, "going to read in: " + filename + "/" + diffFiles);

            BufferedReader buf = new BufferedReader(new FileReader(filename + "/" + diffFiles));
            String line = buf.readLine();
            while (line != null) {
              Log.fine(Log.FAC_SYNC, "reading in line!: {0}", line);
              processNewName(line);
              line = buf.readLine();
            }
            lastReadTime = lastModified;
          } catch (IOException e) {
            Log.warning(
                "Error while executing bash commands to find diffs in repo files: {0}",
                e.getMessage());
          }
          lastReadTime = lastModified;
        } else {
          // System.out.println("i already read in this file.");
        }
      }
    }

    Log.fine(
        Log.FAC_SYNC,
        "checking if the repo backend is new. last modified: {0} repoFileTime: {1} diff: {2} runInterval: {3}",
        lastModified,
        repoFileTime,
        (System.currentTimeMillis() - lastModified),
        runInterval);
    // now check if the file is even new... might have more names to process
    if (lastModified == -1
        || (repoFileTime > lastModified
            && System.currentTimeMillis() - lastModified > runInterval)) {
      Log.fine(
          Log.FAC_SYNC,
          "the repo has a new backend, and the last time the process was run at least one runInterval before");

      lastModified = repoFileTime;
      // there is something new in the backend file... need to process
      Process pr;
      try {
        commandCreateDiffFinal =
            commandCreateDiff.replace("diffNames", "diffNames." + repoFileTime);
        if (lastReadTime != -1)
          commandCreateDiffFinal =
              commandCreateDiffFinal.concat(" ; rm diffNames." + lastReadTime);
        Log.fine(Log.FAC_SYNC, "executing command: " + commandCreateDiffFinal);

        ProcessBuilder pb = new ProcessBuilder();
        pb.directory(repoDir);
        pb.redirectErrorStream(true);
        pb.command("/bin/sh", "-c", commandCreateDiffFinal);
        pr = pb.start();
        pr.waitFor();

        Log.fine(
            Log.FAC_SYNC,
            "done processing the backend, now reading in diffs: "
                + filename
                + "/diffNames."
                + repoFileTime);
        BufferedReader buf =
            new BufferedReader(new FileReader(filename + "/diffNames." + repoFileTime));
        String line = buf.readLine();
        while (line != null) {
          Log.fine(Log.FAC_SYNC, "reading in line!: {0}", line);
          processNewName(line);

          line = buf.readLine();
        }
        lastReadTime = lastModified;
      } catch (IOException e) {
        Log.warning(
            "Error while executing bash commands to find diffs in repo files: {0}",
            e.getMessage());
      } catch (InterruptedException e) {
        Log.warning(
            "Error while waiting for bash commands to find diffs in repo files: {0}",
            e.getMessage());
      }
    } else {
      // there is nothing new, might as well sleep
      // System.out.println("nothing new at this time... going back to sleep");
    }

    try {
      fileLock.release();
    } catch (IOException e) {
      Log.severe(
          "Exception when trying to release lock to process new names for Sync API: {0}. Exiting.",
          e.getMessage());
      System.exit(1);
    }

    try {
      Thread.sleep(runInterval);
    } catch (InterruptedException e) {
      Log.warning("FileBasedSyncMonitor: error while sleeping... {0}", e.getMessage());
    }

    synchronized (runningLock) {
      if (checkShutdown()) {
        keepRunning = false;
        Log.fine(Log.FAC_SYNC, "isRunning was false, time to shut down");
        isRunning = false;
        shutDown = false;
      }
    }
  }
  return;
}

