public static Process execCommand(String cmd) {
  if (cmd == null || cmd.isEmpty()) {
    return null;
  }

  Process p = null;
  try {
    p = new ProcessBuilder(Arrays.asList(cmd.split(" "))).start();
    p.waitFor();
  } catch (Exception e) {
    p = null;
    logger.error("Failed to execute command " + cmd + " : " + e.getMessage());
  }

  return p;
}

private void runScript(ScriptToolModel<?> model) {
  List<String> args = new ArrayList<String>(3 + toolArgs.size());
  if (OSUtil.isWindows()) {
    args.add("cmd.exe");
    args.add("/C");
  }
  args.add(model.getScriptName());
  args.addAll(toolArgs);
  ProcessBuilder processBuilder = new ProcessBuilder(args);
  setupScriptEnvironment(processBuilder, model.getScriptName());
  processBuilder.redirectError(Redirect.INHERIT);
  processBuilder.redirectInput(Redirect.INHERIT);
  if (OSUtil.isWindows()) {
    processBuilder.redirectOutput(Redirect.INHERIT);
  }
  try {
    Process process = processBuilder.start();
    if (!OSUtil.isWindows()) {
      InputStream in = process.getInputStream();
      InputStreamReader inread = new InputStreamReader(in);
      BufferedReader bufferedreader = new BufferedReader(inread);
      String line;
      while ((line = bufferedreader.readLine()) != null) {
        System.out.println(line);
      }
    }
    int exit = process.waitFor();
    if (exit != 0)
      throw new ToolError(
          "Script " + model.getScriptName() + " returned error exit code " + exit) {};
  } catch (IOException e) {
    e.printStackTrace();
  } catch (InterruptedException e) {
    e.printStackTrace();
  }
}

