/**
 * This locates name of the parent of the directory containing the given tool.
 *
 * @param The name of the tool whose directory is being located
 * @return The location of the tool's arch directory, or null if it is not found or if the
 *     architecture is windows
 */
public static String checkLocalToolEnvPath(String toolname) {
  if (org.eclipse.cdt.utils.Platform.getOS().toLowerCase().trim().indexOf("win") >= 0) {
    return null;
  }
  String pPath = null;
  try {
    final Process p =
        new ProcessBuilder("which", toolname)
            .start(); // Runtime.getRuntime().exec("which "+toolname); //$NON-NLS-1$
    BufferedReader reader = new BufferedReader(new InputStreamReader(p.getInputStream()));
    pPath = reader.readLine();
    while (reader.readLine() != null) {}
    reader.close();
    reader = new BufferedReader(new InputStreamReader(p.getErrorStream()));
    while (reader.readLine() != null) {}
  } catch (final IOException e) {
    e.printStackTrace();
  }
  if (pPath == null) {
    return null;
  }
  final File test = new File(pPath);
  final File toolin = new File(toolname);

  if (test.getPath().equals(toolin.getPath())) {
    return null; // TODO: Make sure this is the right behavior when the
  }
  // full path is provided
  if (test.exists()) {
    return test.getParentFile().getPath();
  } else {
    return null;
  }
}

public void export(File outputFile, final Graph graph) throws ExportException {
  // start dot
  final List<String> command = new ArrayList<String>();
  command.add(MyPreferenceStore.getDot());
  command.add("-T" + format);
  command.add("-o");
  command.add(outputFile.getAbsolutePath());

  final ProcessBuilder pb = new ProcessBuilder(command);
  Process dotFilter = null;
  try {
    dotFilter = pb.start();
  } catch (final IOException e) {
    throw new ExportException("Error starting dot: " + e, e);
  }

  try {
    final Process finalDotFilter = dotFilter;

    // graph filtering in another thread, for that we can control it and
    // terminate it
    final Callable<Boolean> filterGraphCall =
        new Callable<Boolean>() {
          public Boolean call() {
            try {
              OutputStream toFilterRaw = finalDotFilter.getOutputStream();
              final BufferedWriter toFilter =
                  new BufferedWriter(new OutputStreamWriter(toFilterRaw));
              graph.printGraph(toFilterRaw, false);
              toFilter.close();
              try {
                finalDotFilter.waitFor();
              } catch (InterruptedException e) {
                finalDotFilter.destroy();
                return false;
              }
              return finalDotFilter.exitValue() == 0;
            } catch (IOException e) {
              return false;
            }
          }
        };
    final FutureTask<Boolean> filterGraphTask = new FutureTask<Boolean>(filterGraphCall);
    new Thread(filterGraphTask, "layout graph").start();

    boolean success = false;
    try {
      success = filterGraphTask.get();
    } catch (final ExecutionException e) {
      if (!(e.getCause() instanceof IOException)) {
        // should not occure (GrappaSupport.filterGraph does only
        // throw IOException)
        throw new RuntimeException(e);
      }
    } catch (final InterruptedException e) {
      Thread.currentThread().interrupt();
      throw new ExportException("Interrupted");
    }
    if (!success) throw new ExportException("Error laying out the graph using dot.");
  } finally {
    if (dotFilter != null) dotFilter.destroy();
  }
}

