/**
 * Run a given command in a subprocess, including threads to copy its stdout and stderr to our
 * stdout and stderr.
 *
 * @param command the command and its arguments
 * @param env the environment to run the process in
 * @return a handle on the process
 * @throws IOException
 */
static Process runClient(List<String> command, Map<String, String> env) throws IOException {
  ProcessBuilder builder = new ProcessBuilder(command);
  if (env != null) {
    builder.environment().putAll(env);
  }
  Process result = builder.start();
  return result;
}

/** process FN with exif and return a string of name: value\r */
static String process(String fn) {
  StringBuilder sb = new StringBuilder();
  try {
    ProcessBuilder pb = new ProcessBuilder("exif", "-m", fn);
    Process p = pb.start();
    InputStream is =
        p.getInputStream(); // "data piped from the standard output stream of the process"
    InputStreamReader isr = new InputStreamReader(is);
    BufferedReader br = new BufferedReader(isr);
    while (true) {
      String s = br.readLine();
      if (s == null) break;
      sb.append(processExifOutput(s));
      sb.append("\n");
    }
    br.close();
    is.close();

  } catch (java.io.IOException e) {
    e.printStackTrace();
  }
  return sb.toString();
}

