      pb.directory(workingDir);
    }
    Process process = pb.start();

    System.out.println("Running " + process.toString());
    new StreamToOutput(process.getErrorStream(), "err").start();
    new StreamToOutput(process.getInputStream(), "std.out").start();

    int returnCode = process.waitFor();
    System.out.println("Runtime process finished with code " + returnCode);
  } catch (Exception e) {
    e.printStackTrace();
  }
}

public static void main(String[] args) throws Throwable {
  showCommittedMemory();

  final int chunkSize = 1024 * 1024 * 100;
  List<byte[]> chunks = new ArrayList<byte[]>(100);
  try {
    for (; ; ) {
      byte[] chunk = new byte[chunkSize];
      touchPages(chunk);
      chunks.add(chunk);
    }
  } catch (OutOfMemoryError e) {
    chunks.set(0, null); // Free up one chunk
    System.gc();
    int size = chunks.size();
    System.out.printf("size=%.2gGB%n", (double) size / 10);

    showCommittedMemory();

    // Can we fork/exec in our current bloated state?
    Process p = new ProcessBuilder("/bin/true").start();
    p.waitFor();
  }
}

