/**
 * Adds coverage information gleamed from running dexdeps on the APK to the {@link ApiCoverage}
 * object.
 *
 * @param apiCoverage object to which the coverage statistics will be added to
 * @param testApk containing the tests that will be scanned by dexdeps
 */
private static void addApiCoverage(ApiCoverage apiCoverage, File testApk)
    throws SAXException, IOException {
  XMLReader xmlReader = XMLReaderFactory.createXMLReader();
  DexDepsXmlHandler dexDepsXmlHandler = new DexDepsXmlHandler(apiCoverage);
  xmlReader.setContentHandler(dexDepsXmlHandler);

  Process process = new ProcessBuilder("dexdeps", "--format=xml", testApk.getPath()).start();
  xmlReader.parse(new InputSource(process.getInputStream()));
}

/** This method executes the specified command. */
public void run() {

  MessageList messages = new MessageList();
  messages.setMessageListSize(getNumberOfMessageLines());

  for (int i = 0; i < getNumberOfMessageLines(); i++) {
    messages.addMessage("");
  }

  String mString = getProcessTitle() + ": <br><br>" + messages.getMessages();

  getViewer().update(mString);

  ProcessBuilder builder = new ProcessBuilder();
  Map<String, String> env = builder.environment();

  if (environment != null) {
    env.putAll(environment);
  }

  builder.command(getCommand());
  builder.directory(new File(getPath()));
  builder.redirectErrorStream(true);

  final Process p;

  try {
    p = builder.start();

    if (getInput() != null) {
      BufferedWriter writer = new BufferedWriter(new OutputStreamWriter(p.getOutputStream()));

      for (String s : getInput()) {
        writer.write(s);
        writer.newLine();
      }

      writer.flush();
    }

    final BufferedReader read = new BufferedReader(new InputStreamReader(p.getInputStream()));

    output = new ArrayList<String>();

    long timeStart = 0;
    long timeStop = 0;

    boolean notFinished = true;
    while (notFinished) {
      try {
        while (read.ready()) {
          String line = read.readLine();
          output.add(line);
          messages.addMessage(line);

          // don't update the message buffer more than
          // ten times per second
          if (timeStop - timeStart > 100 || (timeStart == 0 && timeStop == 0)) {
            timeStart = System.currentTimeMillis();
            if (getViewer() != null) {
              String message = getProcessTitle() + ": <br><br>" + messages.getMessages();
              getViewer().update(message);
            }
          }
          timeStop = System.currentTimeMillis();
        }
        p.exitValue();
        notFinished = false;
      } catch (IOException ex) {
        Logger.getLogger(ProcessTemplate.class.getName()).log(Level.SEVERE, null, ex);
      } catch (IllegalThreadStateException ex) {
        notFinished = true;
      }
    }

    String message = getProcessTitle() + ": <br><br>" + messages.getMessages();
    getViewer().update(message);

  } catch (IOException ex) {
    Logger.getLogger(ProcessTemplate.class.getName()).log(Level.SEVERE, null, ex);
  }
}

public void startSafari() {
  if (safariProcess == null) {
    try {
      setupBuilderEnvironments();

      safariProcess = new ProcessWrapper(processBuilder.start());
      safariProcess.addProcessListener(
          new ProcessListener() {
            public void onProcessFinished() {
              safariProcess = null;
            }
          });

      waitForServerToRespond(new URL(getUrl()), MAX_LOAD_WAIT, safariProcess);
    } catch (Exception e) {
      quit();
      throw new WebDriverException("Failed to launch Safari.", e);
    }
  } else {
    throw new WebDriverException("Safari is already running.");
  }
}

