  String cmd = findWsdlCommand();
  int i = 0;
  for (String location : wsdlLocations) {
    String outputFilename =
        new File(outputDirectory, namespace + (i++) + ".cs").getAbsolutePath();
    String[] command =
        new String[] {
          cmd, serverParameter, "/n:" + namespace, location, "/out:" + outputFilename
        };
    ProcessBuilder builder = new ProcessBuilder();
    builder.redirectErrorStream(true);
    builder.command(command);

    try {
      executeACommand(builder.start());
    } catch (IOException | InterruptedException e) {
      throw new MojoExecutionException(
          "Error, while executing command: " + Arrays.toString(command) + "\n", e);
